(10, 386, 'Admin', 'manoz@outlook.com', '', '82.224.39.132', '2014-03-16 19:08:09', '2014-03-16 19:08:09', 'Je suis un commentaire. J''ai une bordure bleu sur mon avatar parce que je suis l''auteur du post. Je pourrais créer un petit bout de code pour que tout les membres EP aient une bordure ou une goutte sur leur avatar. On verra plus tard.', 0, '1', 'Mozilla/5.0 (Windows NT 6.3; WOW64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/33.0.1750.146 Safari/537.36', '', 0, 1),
(11, 386, 'Admin', 'manoz@outlook.com', '', '82.224.39.132', '2014-03-16 19:08:24', '2014-03-16 19:08:24', 'Je suis une réponse au commentaire du dessus', 0, '1', 'Mozilla/5.0 (Windows NT 6.3; WOW64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/33.0.1750.146 Safari/537.36', '', 10, 1),
(12, 386, 'Admin', 'manoz@outlook.com', '', '82.224.39.132', '2014-03-16 19:08:33', '2014-03-16 19:08:33', 'Idem, belek partie', 0, '1', 'Mozilla/5.0 (Windows NT 6.3; WOW64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/33.0.1750.146 Safari/537.36', '', 11, 1),
(13, 386, 'Autruche', 'prout@outlook.com', '', '82.224.39.132', '2014-03-16 19:10:53', '2014-03-16 19:10:53', 'Moi je m''en cogne, je suis une autruche.', 0, '1', 'Mozilla/5.0 (Windows NT 6.3; WOW64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/33.0.1750.146 Safari/537.36', '', 0, 0),
(14, 386, 'Anonyme', 'prout@mwanoz.fr', '', '82.224.39.132', '2014-03-16 19:11:58', '2014-03-16 19:11:58', 'Je m''en cogne aussi.', 0, '1', 'Mozilla/5.0 (Windows NT 6.3; WOW64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/33.0.1750.146 Safari/537.36', '', 13, 0);
